module.exports = function _router (state) {
  if (!ROUTER) {
    ROUTER        = routes()
    var hHeader   = Header(state)
    var hContent  = Content(state)
    var hFooter   = Footer(state)

    ROUTER.addRoute('/', function (state) {
      return h('div', [
        hHeader(state),
        hContent(state),
        hFooter(state)
      ])
    })

  }
  return ROUTER
}
